import React, { useEffect, useMemo, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import Comments from '../components/Comments';
import { API_BASE, API_ORIGIN } from '../config';
import { useAuth } from '../context/AuthContext';

export default function TourismDetail() {
  const { category, slug } = useParams();
  const { user, token } = useAuth();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [cat, setCat] = useState(null);
  const [entry, setEntry] = useState(null);
  const [editing, setEditing] = useState(false);
  const [detailsText, setDetailsText] = useState('');
  const isAdmin = !!user && (user.role === 'admin' || user.role === 'superadmin');

  useEffect(() => {
    let alive = true;
    setLoading(true); setError(''); setCat(null); setEntry(null);
    (async () => {
      try {
        const r = await fetch(`${API_BASE}/tourism/categories/${encodeURIComponent(category)}/entries/${encodeURIComponent(slug)}`);
        if (!r.ok) throw new Error('Not found');
        const data = await r.json();
        if (!alive) return;
        setCat(data.category || null);
        setEntry(data.entry || null);
        const c = (data.entry && data.entry.content_json) || {};
        setDetailsText(String(c.details || c.body || ''));
      } catch (e) {
        if (!alive) return;
        setError(e.message || 'Error');
      } finally {
        if (alive) setLoading(false);
      }
    })();
    return () => { alive = false; };
  }, [category, slug]);

  if (loading) {
    return (
      <div className="max-w-3xl mx-auto p-6">
        <div className="text-sm text-gray-600">Loading...</div>
      </div>
    );
  }
  if (error || !entry) {
    return (
      <div className="max-w-3xl mx-auto p-6">
        <div className="text-xl font-semibold mb-2">Item not found</div>
        <Link to="/tourism" className="text-blue-700 hover:underline">Back to Tourism</Link>
      </div>
    );
  }

  const content = entry.content_json || {};
  const title = entry.title || content.name || '—';
  const hero = content.image || (Array.isArray(content.images) && content.images[0]) || (Array.isArray(content.gallery) && content.gallery[0]) || null;
  const shortDesc = content.description || '';
  const details = content.details || content.body || '';
  const myBarangayId = user?.barangayId || user?.barangay_id || null;
  const canEdit = isAdmin || (!!user && user.role === 'barangay_captain' && myBarangayId && Number(myBarangayId) === Number(entry.barangay_id));

  async function saveDetails() {
    try {
      const fd = new FormData();
      const nextContent = { ...content, details: detailsText };
      fd.append('content', JSON.stringify(nextContent));
      const r = await fetch(`${API_BASE}/tourism/entries/${entry.id}`, { method: 'PUT', headers: { Authorization: `Bearer ${token}` }, body: fd });
      if (!r.ok) {
        const e = await r.json().catch(()=>({}));
        throw new Error(e.error || 'Failed to save');
      }
      setEntry(prev => prev ? { ...prev, content_json: nextContent } : prev);
      setEditing(false);
    } catch (e) {
      alert(e.message || 'Failed to save');
    }
  }

  return (
    <div className="max-w-3xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <Link to="/tourism" className="text-blue-700 hover:underline text-sm">← Back to Tourism</Link>
      <article className="mt-3 border rounded-md overflow-hidden bg-white">
        {hero && <img src={`${String(hero).match(/^https?:|^data:/) ? String(hero) : API_ORIGIN + String(hero)}`} alt="" className="w-full h-56 object-cover" />}
        <div className="p-4">
          <h1 className="text-2xl font-semibold">{title}</h1>
          <div className="text-sm text-gray-600">{entry.barangay_name ? `Barangay ${entry.barangay_name}` : ''}</div>
          {shortDesc && <p className="text-sm text-gray-700 mt-2">{shortDesc}</p>}
          <div className="mt-4">
            <div className="flex items-center justify-between">
              <h2 className="text-lg font-medium">Details</h2>
              {canEdit && !editing && (
                <button onClick={()=>{ setEditing(true); setDetailsText(details || ''); }} className="text-sm px-3 py-1.5 rounded-md border bg-white hover:bg-gray-50">{details ? 'Edit' : 'Add'} description</button>
              )}
            </div>
            {!editing ? (
              details ? (
                <p className="text-sm text-gray-700 mt-2 whitespace-pre-wrap">{details}</p>
              ) : (
                <p className="text-sm text-gray-500 mt-2 italic">Click edit to add description</p>
              )
            ) : (
              <div className="mt-2 space-y-2">
                <textarea value={detailsText} onChange={(e)=>setDetailsText(e.target.value)} rows={6} className="w-full border rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500" placeholder="Write detailed information about this entry..." />
                <div className="flex items-center gap-2 justify-end">
                  <button onClick={()=>{ setEditing(false); setDetailsText(details || ''); }} className="px-3 py-1.5 rounded-md border">Cancel</button>
                  <button onClick={saveDetails} className="px-3 py-1.5 rounded-md bg-blue-600 text-white hover:bg-blue-700">Save</button>
                </div>
              </div>
            )}
          </div>
        </div>
      </article>
      <Comments threadId={`tourism:${cat?.slug || category}:${entry.slug}`} title="Discussions & Inquiries" />
    </div>
  );
}
