import React, { useState } from 'react';
import services from '../data/services';

export default function Services() {
  const [query, setQuery] = useState('');
  const filtered = services.filter(s => s.title.toLowerCase().includes(query.toLowerCase()));
  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <h1 className="text-2xl font-semibold mb-4">Government Services</h1>
      <input value={query} onChange={(e)=>setQuery(e.target.value)} placeholder="Filter by service name..." className="w-full md:w-1/2 border rounded-md px-3 py-2 mb-4" />
      <div className="grid md:grid-cols-2 gap-4">
        {filtered.map((svc) => (
          <details key={svc.slug} className="border rounded-md p-4">
            <summary className="font-medium cursor-pointer">{svc.title} <span className="text-sm text-gray-500">— {svc.office}</span></summary>
            <div className="mt-3 text-sm text-gray-700">
              <p className="mb-2">{svc.description}</p>
              <div className="mb-2"><span className="font-medium">Office:</span> {svc.office} — Head: {svc.head} — Location: {svc.location} — Contact: {svc.contact}</div>
              <div className="mb-2">
                <div className="font-medium">Requirements</div>
                <ul className="list-disc ml-5">
                  {svc.requirements.map((r,i)=>(<li key={i}>{r}</li>))}
                </ul>
              </div>
              <div>
                <div className="font-medium">Step-by-step Process</div>
                <ol className="list-decimal ml-5">
                  {svc.steps.map((st,i)=>(<li key={i}>{st}</li>))}
                </ol>
              </div>
            </div>
          </details>
        ))}
      </div>
    </div>
  );
}
