import React, { useEffect, useMemo, useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import { API_BASE } from '../../config';

const FIELD_TYPES = [
  { value: 'text', label: 'Text' },
  { value: 'textarea', label: 'Textarea' },
  { value: 'richtext', label: 'Rich Text' },
  { value: 'number', label: 'Number' },
  { value: 'date', label: 'Date' },
  { value: 'link', label: 'Link' },
  { value: 'image', label: 'Image' },
  { value: 'file', label: 'File' },
  { value: 'images', label: 'Images' },
  { value: 'files', label: 'Files' },
  { value: 'gallery', label: 'Gallery' },
];

export default function CategoryForm({ category, onClose, onSaved }) {
  const isEdit = !!category;
  const { token } = useAuth();
  const [title, setTitle] = useState(category?.title || '');
  const [slug, setSlug] = useState(category?.slug || '');
  const [description, setDescription] = useState(category?.description || '');
  const [fields, setFields] = useState(() => Array.isArray(category?.schema_json?.fields) ? category.schema_json.fields : []);
  const [layout, setLayout] = useState(() => category?.layout_json || { style: 'list' });
  const [submitting, setSubmitting] = useState(false);

  function addField() {
    setFields(prev => [...prev, { key: '', label: '', type: 'text', required: false }]);
  }
  function removeField(idx) {
    setFields(prev => prev.filter((_, i) => i !== idx));
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setSubmitting(true);
    try {
      const payload = {
        title: title.trim(),
        slug: slug.trim() || undefined,
        description: description || null,
        schema: { fields: fields.map(f => ({ key: String(f.key).trim(), label: String(f.label || f.key).trim(), type: f.type, required: !!f.required })) },
        layout,
      };
      const url = isEdit ? `${API_BASE}/gov/categories/${category.id}` : `${API_BASE}/gov/categories`;
      const method = isEdit ? 'PUT' : 'POST';
      const r = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${token}` },
        body: JSON.stringify(payload),
      });
      if (!r.ok) {
        const e = await r.json().catch(()=>({}));
        throw new Error(e.error || 'Failed to save category');
      }
      onSaved?.();
      onClose?.();
    } catch (err) {
      alert(err.message || 'Error');
    } finally {
      setSubmitting(false);
    }
  }

  return (
    <form onSubmit={handleSubmit} className="space-y-4 p-1 sm:p-2">
      <div>
        <label className="block text-sm text-gray-700">Title</label>
        <input value={title} onChange={(e)=>setTitle(e.target.value)} className="w-full border rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500" required />
      </div>
      <div>
        <label className="block text-sm text-gray-700">Slug (optional)</label>
        <input value={slug} onChange={(e)=>setSlug(e.target.value)} className="w-full border rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500" placeholder="auto if empty" />
      </div>
      <div>
        <label className="block text-sm text-gray-700">Description</label>
        <textarea value={description} onChange={(e)=>setDescription(e.target.value)} rows={2} className="w-full border rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500" />
      </div>
      <div>
        <div className="flex items-center justify-between mb-2">
          <label className="block text-sm font-medium text-gray-700">Fields</label>
          <button type="button" onClick={addField} className="text-xs px-2 py-1 rounded-md border bg-white">Add Field</button>
        </div>
        <div className="space-y-3">
          {fields.map((f, idx) => (
            <div key={idx} className="border rounded-md p-3">
              <div className="grid sm:grid-cols-4 gap-3">
                <div>
                  <label className="block text-xs text-gray-700">Key</label>
                  <input value={f.key} onChange={(e)=>setFields(prev=>prev.map((x,i)=> i===idx?{...x,key:e.target.value}:x))} className="w-full border rounded-md px-2 py-1 text-sm" placeholder="e.g., name" />
                </div>
                <div>
                  <label className="block text-xs text-gray-700">Label</label>
                  <input value={f.label} onChange={(e)=>setFields(prev=>prev.map((x,i)=> i===idx?{...x,label:e.target.value}:x))} className="w-full border rounded-md px-2 py-1 text-sm" placeholder="e.g., Name" />
                </div>
                <div>
                  <label className="block text-xs text-gray-700">Type</label>
                  <select value={f.type} onChange={(e)=>setFields(prev=>prev.map((x,i)=> i===idx?{...x,type:e.target.value}:x))} className="w-full border rounded-md px-2 py-1 text-sm">
                    {FIELD_TYPES.map(t => (<option key={t.value} value={t.value}>{t.label}</option>))}
                  </select>
                </div>
                <div className="flex items-end">
                  <label className="inline-flex items-center gap-2 text-xs text-gray-700">
                    <input type="checkbox" checked={!!f.required} onChange={(e)=>setFields(prev=>prev.map((x,i)=> i===idx?{...x,required:e.target.checked}:x))} /> Required
                  </label>
                </div>
              </div>
              <div className="flex justify-end mt-2">
                <button type="button" onClick={()=>removeField(idx)} className="text-xs px-2 py-1 rounded-md border bg-white">Remove</button>
              </div>
            </div>
          ))}
          {fields.length === 0 && <div className="text-xs text-gray-500">No fields yet. Click Add Field.</div>}
        </div>
      </div>
      <div className="flex justify-end gap-2">
        <button type="button" onClick={onClose} className="px-4 py-2 rounded-md border">Cancel</button>
        <button disabled={submitting} className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700">{submitting ? 'Saving...' : (isEdit ? 'Save' : 'Create')}</button>
      </div>
    </form>
  );
}
