import React, { useEffect, useState } from 'react';

function loadComments(key) {
  try {
    const s = localStorage.getItem(key);
    return s ? JSON.parse(s) : [];
  } catch {
    return [];
  }
}

function saveComments(key, data) {
  try {
    localStorage.setItem(key, JSON.stringify(data));
  } catch {}
}

export default function Comments({ threadId, title = 'Comments' }) {
  const storageKey = `comments:${threadId}`;
  const [items, setItems] = useState(() => loadComments(storageKey));
  const [name, setName] = useState('');
  const [text, setText] = useState('');
  const [anon, setAnon] = useState(false);

  useEffect(() => {
    saveComments(storageKey, items);
  }, [items]);

  const add = (e) => {
    e.preventDefault();
    if (!text.trim()) return;
    const author = anon || !name.trim() ? 'Anonymous' : name.trim();
    const comment = { id: Date.now().toString(), author, text: text.trim(), ts: new Date().toISOString() };
    setItems([comment, ...items]);
    setText('');
  };

  return (
    <div className="mt-6">
      <h3 className="font-semibold mb-2">{title}</h3>
      <form onSubmit={add} className="space-y-2 border rounded-md p-3">
        <div className="flex items-center gap-2">
          <input
            className="flex-1 border rounded-md px-3 py-2"
            placeholder="Write a comment..."
            value={text}
            onChange={(e)=>setText(e.target.value)}
          />
        </div>
        <div className="flex items-center justify-between text-sm">
          <div className="flex items-center gap-2">
            <label className="flex items-center gap-1">
              <input type="checkbox" checked={anon} onChange={(e)=>setAnon(e.target.checked)} />
              Post anonymously
            </label>
            {!anon && (
              <input
                className="border rounded-md px-2 py-1"
                placeholder="Your name (optional)"
                value={name}
                onChange={(e)=>setName(e.target.value)}
              />
            )}
          </div>
          <button className="bg-blue-600 text-white px-3 py-1.5 rounded-md hover:bg-blue-700">Comment</button>
        </div>
      </form>

      <div className="mt-4 space-y-3">
        {items.length === 0 && (
          <div className="text-sm text-gray-600">No comments yet.</div>
        )}
        {items.map(c => (
          <div key={c.id} className="border rounded-md p-3">
            <div className="text-xs text-gray-500">{new Date(c.ts).toLocaleString()}</div>
            <div className="text-sm"><span className="font-medium">{c.author}</span>: {c.text}</div>
          </div>
        ))}
      </div>
    </div>
  );
}
